import { useState } from "react"
import foodData from "./data/foodData.json"
import {
  DndContext,
  DragOverlay,
  KeyboardSensor,
  MouseSensor,
  TouchSensor,
  useSensor,
  useSensors,
} from "@dnd-kit/core"
import { sortableKeyboardCoordinates } from "@dnd-kit/sortable"
import {
  arrayMove,
  insertAtIndex,
  removeAtIndex,
} from "./utils/arrayOperations"
import { Board } from "./components/Board"
import { Item } from "./components/Item"
import { Header } from "./components/Header"
import { InfoBar } from "./components/InfoBar"

function App() {
  const [dishGroups, setDishGroups] = useState<Record<string, string[]>>({
    breakfast: [
      "https://www.cathysglutenfree.com/wp-content/uploads/2022/01/fruit-cocktail-salad-f-jpg-720x720.jpg",
      "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSOnM4v-w_3nZIpp52piebBevJwSsmS-oe_j3fMukdJ5H83sSErygP_wVv4BGqTKEuUvPc&usqp=CAU",
      "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSKDQQvQUxXvq4fBuCzjt8tlfjV7w-U-3tSxjJ3W6t9EnHSFoYEldzQlDQT0T11aVtjPhE&usqp=CAU",
      "https://imagesvc.meredithcorp.io/v3/mm/image?url=https%3A%2F%2Fimg1.cookinglight.timeinc.net%2Fsites%2Fdefault%2Ffiles%2Fstyles%2Fmedium_2x%2Fpublic%2Fimage%2F2016%2F09%2Fmain%2F1610p101-brandied-preserved-figs-1.jpg%3Fitok%3D0xXsqP-5",
      "https://afarmgirlinthemaking.com/wp-content/uploads/2020/01/canning-grapefruit-12-1024x768.jpg",
    ],
    morningsnack: [
      "https://www.acouplecooks.com/wp-content/uploads/2021/09/Boil-Potatoes-009s.jpg",
      "https://www.recipetineats.com/wp-content/uploads/2022/09/Crispy-Fries_8.jpg",
      "https://www.allrecipes.com/thmb/jOPKFsr6fvXjxAaRXThxTL6UTv8=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/1070416-a1a6c982aaef4da3acfdda0ae7f3b87f.jpg",
      "https://kirbiecravings.com/wp-content/uploads/2017/11/mashed-potato-waffles-4.jpg",
      "https://i.pinimg.com/736x/60/93/72/609372b16669e547cb8bf8e13dde876c.jpg",
    ],
    lunch: [
      "https://imagesvc.meredithcorp.io/v3/mm/image?url=https%3A%2F%2Fstatic.onecms.io%2Fwp-content%2Fuploads%2Fsites%2F44%2F2022%2F04%2F06%2Fcrab-salad.jpg&q=60",
      "https://cdn.schwans.com/media/images/products/62172-1-1540.jpg",
      "https://www.grocery.coop/sites/default/files/wp-content/uploads/2011/06/Brie_11.jpg",
      "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSTESIeli2V5qUadRfURcb976BEU7BuXXibnQ&usqp=CAU",
      "data:image/jpeg;base64,",
    ],
    afternoonsnack: [
      "https://www.aspicyperspective.com/wp-content/uploads/2019/10/How-To-Make-Buttermilk-13.jpg",
      "https://eatplant-based.com/wp-content/uploads/2022/04/Soy-Milk.jpg",
      "https://loseweightbyeating.com/wp-content/uploads/2014/05/AdobeStock_191079029-scaled.jpeg",
      "data:image/jpeg;base64,",
      "https://assets.epicurious.com/photos/560d99ccf9a84192308a1ae8/master/pass/238776.jpg",
    ],
    dinner: [
      "https://thefoodiediaries.co/wp-content/uploads/2020/05/img_1081.jpg",
      "https://www.giallozafferano.com/images/255-25509/Yogurt-cream_1200x800.jpg",
      "https://tmbidigitalassetsazure.blob.core.windows.net/rms3-prod/attachments/37/1200x1200/Pasta-Crab-Salad_exps15484_PRC1753658D75_RMS.jpg",
      "https://www.simplysissom.com/wp-content/uploads/2017/01/horizontalparfaityogurt-copy.jpg",
      "https://eatdelights.com/wp-content/uploads/2021/06/what-does-asiago-cheese-taste-like-240x320.jpg",
    ],
  })

  const [activeId, setActiveId] = useState(null)

  const [over, setOver] = useState(null)

  const sensors = useSensors(
    useSensor(MouseSensor),
    useSensor(TouchSensor),
    useSensor(KeyboardSensor, { coordinateGetter: sortableKeyboardCoordinates })
  )

  const handleDragStart = ({ active }: any) => setActiveId(active.id)
  const handleDragCancel = () => setActiveId(null)
  const handleDragOver = ({ active, over }: any) => {
    const overId = over?.id
    if (!overId) return

    const activeContainer = active.data.current.sortable.containerId
    const overContainer = over.data.current?.sortable.containerId || over.id
    if (activeContainer === overContainer) return
    setOver(overContainer)
  }

  const handleDragEnd = ({ active, over }: any) => {
    if (!over) {
      setActiveId(null)
      return
    }

    if (active.id !== over.id) {
      const activeContainer = active.data.current.sortable.containerId
      const overContainer = over.data.current?.sortable.containerId || over.id
      const activeIndex = active.data.current.sortable.index
      const overIndex =
        over.id in dishGroups
          ? dishGroups[overContainer].length + 1
          : over.data.current.sortable.index
      setDishGroups((dishGroups) => {
        let newDishes
        if (activeContainer === overContainer) {
          newDishes = {
            ...dishGroups,
            [overContainer]: arrayMove(
              dishGroups[overContainer],
              activeIndex,
              overIndex
            ),
          }
        } else {
          newDishes = moveBetweenContainers(
            dishGroups,
            activeContainer,
            activeIndex,
            overContainer,
            overIndex,
            active.id
          )
        }

        return newDishes
      })
    }
    setActiveId(null)
    setOver(null)
  }

  const moveBetweenContainers = (
    items: Record<string, string[]>,
    activeContainer: string,
    activeIndex: number,
    overContainer: string,
    overIndex: number,
    item: string
  ) => {
    return {
      ...items,
      [activeContainer]: removeAtIndex(items[activeContainer], activeIndex),
      [overContainer]: insertAtIndex(items[overContainer], overIndex, item),
    }
  }
  return (
    <DndContext
      sensors={sensors}
      onDragStart={handleDragStart}
      onDragCancel={handleDragCancel}
      onDragOver={handleDragOver}
      onDragEnd={handleDragEnd}
    >
      <main className="py-10 flex flex-col justify-center items-center bg-light-grey">
        <section className="w-3/4 h-2/3">
          <h1 className="text-left mb-4 text-xl text-dark-blue font-bold ">
            Zenfit Meal [v2]
          </h1>
          <div className="bg-white drop-shadow-md m-3 pb-5">
            <Header />
            <InfoBar />
            <div className="flex border border-light-grey mb-5 m-3">
              {Object.keys(dishGroups).map((group) => (
                <Board
                  id={group}
                  dishes={dishGroups[group]}
                  activeId={activeId}
                  over={over}
                  key={group}
                />
              ))}
            </div>
          </div>
          <DragOverlay>
            {activeId ? <Item id={activeId} dragOverlay /> : null}
          </DragOverlay>
        </section>
      </main>
    </DndContext>
  )
}

export default App
